""" Base Class For Music DB Master Classes """__all__ = ["MusicDBParamsBase"]class MusicDBParamsBase:    def __repr__(self):        return "MusicDBParamsBase"        def __init__(self, **kwargs):        self.verbose = kwargs.get('verbose', False)                # Basic Params        self.db = None        self.dir = None        self.short = None            # Group Types        self.parseMap = {}        self.concatMap = {}        self.mergeMap = {}        self.groupMap = {}                # Metadata Media Ranking        self.mediaRanking = {}                # Match Omits        self.omit = {}