""" Base Class For Music DB Master Classes """__all__ = ["MusicDBParamsBase"]class MusicDBParamsBase:    def __repr__(self):        return "MusicDBParamsBase"        def __init__(self, **kwargs):        self.verbose = kwargs.get('verbose', False)                #######################################################################        # Basic        #######################################################################        self.db = None        self.dir = None        self.short = None        #######################################################################        # DBID        #######################################################################        self.dbidPatterns = []                #######################################################################        # MediaUtils        #######################################################################        self.mediaTypes = []        self.mediaTypeIDReqs = {}        self.mediaTypeData = {}            #######################################################################        # Group Types        #######################################################################        self.parseMap = {}        self.concatMap = {}        self.mergeMap = {}        #######################################################################        # Metadata Media Ranking        #######################################################################        self.mediaRanking = {}        #######################################################################        # Metadata Rules        #######################################################################        self.metaRules = {"Link": {}, "Genre": {}, "Bio": {}, "Metric": {}}                #######################################################################        # Media Summary        #######################################################################        self.mediaSummary = {"Media": {}, "Artist": {}}                #######################################################################        # Match Omit        #######################################################################        self.omit = {}